mapper.select:
select  ID from TB_TRAVEL


        TAKEN_TIME >= #{start}


        AND TAKEN_TIME <= #{end}


ORDER BY TAKEN_TIME ASC

mapper.update:
update TB_TRAVEL set LATITUDE = #{lat}, LONGITUDE = #{lon},
                    GCJ_LAT = #{gcjLat}, GCJ_LON = #{gcjLon}
                    where ID =#{id}
